class Image {

  constructor(selectedImage, imageId=null, userId=null, sessionvalue=null) {
    fetch( 'http://173.255.247.69:5000/get_image?imageId=' + imageId
       + "&selected_image=" + selectedImage + "&user_id=" 
       + userId + "&sessionvalue=" + this.props.user.sessionvalue, {
      credentials : 'same-origin',
      method: 'GET',
      headers: { 'Accept': 'application/json', },
	})
	.then(response => response.json())
         .then(data =>  {
           return {
             mainPicPath : data.imagePath,
             mainPicCaption : data.caption,
             flaskMessage : data.message,
             imagePosition : data.imagePosition,
             imageId : data.image_id,
           }
         })
         .catch(error => {
           this.setState({ error, isLoading: false});
           console.log("error: ", error);
        });
  }

}

export default Image
